CREATE TABLE categoria (ID BIGINT AUTO_INCREMENT NOT NULL UNIQUE, NOMBRE VARCHAR(120), PRIMARY KEY (ID))
CREATE TABLE PRODUCT (ID INTEGER AUTO_INCREMENT NOT NULL, CATEGORIA VARCHAR(255), DESCRIPTION VARCHAR(255), ESTADO VARCHAR(255), NAME VARCHAR(255), PRICE DOUBLE, PRIMARY KEY (ID))
CREATE TABLE MENU (ID INTEGER AUTO_INCREMENT NOT NULL, DESCRIPCION VARCHAR(255), ESTADO VARCHAR(255), ID_DIV_INTERNO VARCHAR(255), LABEL VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE OPCION (ID INTEGER AUTO_INCREMENT NOT NULL, ESTADO VARCHAR(255), LABEL VARCHAR(255), URI VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE ROL (ID INTEGER AUTO_INCREMENT NOT NULL, DESCRIPCION VARCHAR(255), ESTADO VARCHAR(255), NOMBRE VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE USUARIO (ID INTEGER AUTO_INCREMENT NOT NULL, ESTADO VARCHAR(255), PASSWORD VARCHAR(255), USUARIO VARCHAR(255), ROL_ID INTEGER, PRIMARY KEY (ID))
CREATE TABLE OPCIONES_MENU (MENU_ID INTEGER NOT NULL, OPCION_ID INTEGER NOT NULL, PRIMARY KEY (MENU_ID, OPCION_ID))
CREATE TABLE MENUS_ROL (ROL_ID INTEGER NOT NULL, MENU_ID INTEGER NOT NULL, PRIMARY KEY (ROL_ID, MENU_ID))
ALTER TABLE USUARIO ADD CONSTRAINT FK_USUARIO_ROL_ID FOREIGN KEY (ROL_ID) REFERENCES ROL (ID)
ALTER TABLE OPCIONES_MENU ADD CONSTRAINT FK_OPCIONES_MENU_MENU_ID FOREIGN KEY (MENU_ID) REFERENCES MENU (ID)
ALTER TABLE OPCIONES_MENU ADD CONSTRAINT FK_OPCIONES_MENU_OPCION_ID FOREIGN KEY (OPCION_ID) REFERENCES OPCION (ID)
ALTER TABLE MENUS_ROL ADD CONSTRAINT FK_MENUS_ROL_ROL_ID FOREIGN KEY (ROL_ID) REFERENCES ROL (ID)
ALTER TABLE MENUS_ROL ADD CONSTRAINT FK_MENUS_ROL_MENU_ID FOREIGN KEY (MENU_ID) REFERENCES MENU (ID)
